const flagDropdown = document.getElementById('flagDropdown');
const selectedFlagDiv = document.getElementById('selectedFlag');

const flagImageURLs = {
    us: 'data:image/png;base64,', 
    ca: 'data:image/png;base64,',
    uk: 'data:image/png;base64,',
    
};

const defaultFlagImage = document.createElement('img');
defaultFlagImage.src = flagImageURLs['us']; 
defaultFlagImage.alt = 'us'; 
defaultFlagImage.width = 50; 
defaultFlagImage.height = 30;
selectedFlagDiv.appendChild(defaultFlagImage);

flagDropdown.addEventListener('change', function() {
const selectedFlagValue = flagDropdown.value;
const flagImageUrl = flagImageURLs[selectedFlagValue];
const flagImage = document.createElement('img');
flagImage.src = flagImageUrl;
flagImage.alt = selectedFlagValue;
flagImage.width = 50; 
flagImage.height = 30;
selectedFlagDiv.innerHTML = '';
selectedFlagDiv.appendChild(flagImage);
});
